class Student {
    firstName: string
    lastName: string
    age: number
    courses: string[]

    constructor(first: string, last: string, age: number, courses: string[]) {
        this.firstName = first
        this.lastName = last
        this.age = age
        this.courses = courses
    }

    enrol(coursesName: string) {
        this.courses.push(coursesName)
    }

    listCourse(){
        return this.courses.slice()
    }
}

const student = new Student('Bob', 'MS', 24, ['Angular'])
student.enrol('React')


// Interfaces

interface Human {
    firstName: string
    age: number

    greet: () => void
}

let bob: Human

bob = {
    firstName: 'Bob',
    age: 24,
    greet(){
        console.log('Hello!');
        
    }
}

class Instructor implements Human {
    firstName: string;
    age: number
    greet(){
        console.log('Hello!');
        
    }
}